nan: Example and Test
# include <cppad/nan.hpp> # include <vector>
bool nan(void) { bool ok = true;
// get a nan double double_zero = 0.; double double_nan = CppAD::nan(double_zero);
// create a simple vector with no nans std::vector<double> v(2); v[0] = double_zero; v[1] = double_zero;
// check that zero is not nan ok &= ! CppAD::isnan(double_zero); ok &= ! CppAD::hasnan(v);
// check that nan is a nan v[1] = double_nan; ok &= CppAD::isnan(double_nan); ok &= CppAD::hasnan(v);
return ok; }
