sacado_det_minor.cpp
Headings-> link_det_minor
Sacado Speed: Gradient of Determinant by Minor Expansion
link_det_minor
# include <vector> # include <Sacado.hpp> # include <cppad/speed/det_by_minor.hpp> # include <cppad/speed/uniform_01.hpp>
bool link_det_minor( size_t size , size_t repeat , CppAD::vector<double> &matrix , CppAD::vector<double> &gradient ) { // ----------------------------------------------------- // setup
// object for computing determinant typedef Sacado::Rad::ADvar<double> ADScalar; typedef CppAD::vector<ADScalar> ADVector; CppAD::det_by_minor<ADScalar> Det(size);
size_t i; // temporary index size_t n = size * size; // number of independent variables ADScalar detA; // AD value of the determinant ADVector A(n); // AD version of matrix
// ------------------------------------------------------ while(repeat--) { // get the next matrix CppAD::uniform_01(n, matrix);
// set independent variable values for(i = 0; i < n; i++) A[i] = matrix[i];
// compute the determinant detA = Det(A);
// Compute the gradient of detA ADScalar::Gradcomp();
// return gradient using reverse mode for(i =0; i < n; i++) gradient[i] = A[i].adj(); // partial detA w.r.t A[i] } // --------------------------------------------------------- return true; }
